specialChar=["0","00"],operationChar=[".","DEL","*","-","+","=","/"],$(document).ready(function(){var opScreen=$("#display p"),resScreen=$("#results p");$("#numbers .button").click(function(){keyVal=$(this).children("p").text(),currentValue=opScreen.text(),"0"===currentValue?specialChar.includes(keyVal)||operationChar.includes(keyVal)||opScreen.text(keyVal):currentValue.length<=10&&("."===keyVal&&currentValue.includes(".")?opScreen.text(currentValue):opScreen.text(currentValue+keyVal))}),$("#operators .button").click(function(){if(currentValue=opScreen.text(),keyVal=$(this).children("p").text(),"="===keyVal)resScreen.text(eval(currentValue));else if("DEL"===keyVal)resScreen.text("0"),opScreen.text("0");else if(currentValue.length<=9){var lastDigit=currentValue.substring(currentValue.length-1);operationChar.includes(lastDigit)?opScreen.text(currentValue):opScreen.text(currentValue+keyVal)}})});